import axios from 'axios';

let apiURL;
if ('development' === 'development') {
  apiURL = 'https://tindev-app.herokuapp.com';
} else if (process.env.NODE_ENV === 'production') {
  apiURL = process.env.REACT_APP_API_URL;
}

const api = axios.create({
  baseURL: apiURL,
});

export default api;
